Method.Implementation:
#dim %request As %CSP.Request #dim obj As %ZEN.proxyObject
return:(pMethod'="POST") $$$OK
// Convert request body into UTF8 proxyObject set content = %request.Content.Read($$$MaxStringLength) set content = $ZCVT(content,"I","UTF8") set st = ##class(%ZEN.Auxiliary.jsonProvider).%ConvertJSONToObject(content,,.obj,1) return:$$$ISERR(st) st return:'$IsObject(obj) $$$ERROR($$$ArgumentIsNotAnObject,"Body")
return:'$d(obj.%data(pBody)) $$$ERROR($$$PropertyRequired,pBody)
set %request.Content = obj // obj here is a valid UTF8 proxyObject with required property return $$$OK

set sql = ##class(MDX2JSON.Dashboard).GetDashboardListSQL()
try {        
    do ##class(%ZEN.Auxiliary.jsonSQLProvider).%WriteJSONFromSQL(,sql)
} catch ex {
    write $$$ZENJSSTR($System.Status.GetErrorText(ex.AsStatus()))
}
return $$$OK

try {
    set st = ##class(MDX2JSON.Utils).WriteJSONfromMDX(%request.Content.MDX)
} catch ex {
    set st=ex.AsStatus()
}

return st
